	content: '';
	position: absolute;
	top: 0;
	bottom: 0;
	left: 0;
	right: 0;
	filter: blur(20px);
	z-index: -1;
	margin: -30px;
}
@keyframes qbounce {
      0%, 100% {
        transform: scale(1);
      }
      25% {
        transform: scale(0.9);
      }
      50% {
        transform: scale(1.1);
      }
      75% {
        transform: scale(0.95);
      }
